@Component({
  selector: 'app-listado',
  templateUrl: './listado.component.html',
})

export class ListadoComponent{

  heroes: string[] = ['Spiderman', 'Ironman','Hulk', 'Thor'];

  borrarHeroe(){
    console.log('borrando...');
    this.heroes.shift();
  }
}
